import React, { Component } from "react";
import Util from "../../../Util";
import Icon from "./Icon";
import { Info } from "../../../comman/Info";
import UrlInput from "../../../comman/UrlInput";
export default class Text extends Component {
  state = {
    items: this.props.items,
    iconActive: false,
  };

  componentWillReceiveProps(nextProps) {
    if (Util.isRedirected) {
      Util.isRedirected = false;
      this.setState({ items: nextProps.items });
    }
  }

  componentWillUnmount() {
    this.props.onChange({ items: this.state.items });
  }

  onAddClick = () => {
    const { items } = this.state;
    items.push({ icon: "", text: "", link: "" });
    this.setState({ items });
  };

  removeClick = (index) => {
    const { items } = this.state;
    items.splice(index, 1);
    this.setState({ items });
    this.props.onChange({ items });
  };

  onChange = (value, index, field) => {
    let { items } = this.state;
    items[index][field] = value;
    this.setState({ items });
    this.props.onChange({ items });
  };

  selectIcon = (index) => {
    console.log(index);
    this.setState({ iconActive: index + 1 });
  };

  onSelection = (icon) => {
    let { items, iconActive } = this.state;
    items[iconActive - 1]["icon"] = icon;
    this.setState({ items, iconActive: 0 });
  };

  renderList = (item, index) => {
    return (
      <div className="row" style={{ border: "1px solid #eeeeee", paddingBottom: "2px" }}>
        <div className="col-12" style={{ paddingBottom: "0px" }}>
          <div className="float-right">
            <button disabled={index === 0 && this.state.items.length === 1 ? true : false} onClick={() => this.removeClick(index)} className="btn btn-sm btn-warning">
              Remove
            </button>
          </div>
        </div>
        <div className="col-6">
          <div className="form-element" onClick={() => this.selectIcon(index)}>
            <div className="label-text">
              Icon*
              <Info text="Select Icon for Dynemic element " />
            </div>
            <input type="text" onChange={(e) => this.onChange(e.target.value, index, "icon")} className="form-control" value={item.icon} readonly="readonly" />
          </div>
        </div>
        <div className="col-6">
          <div className="form-element">
            <div className="label-text">
              Text*
              <Info text="enter text for dynamic element " />
            </div>
            <input type="text" className="form-control" onChange={(e) => this.onChange(e.target.value, index, "text")} value={item.text} />
          </div>
        </div>
        <div className="col-12">
          <div className="form-element">
            <div className="label-text">
              Link*
              <Info text="Url to redirect" />
            </div>
            <UrlInput type="text" isTab={true} className="form-control" onChange={(e) => this.onChange(e.target.value, index, "link")} value={item.link} />
          </div>
        </div>
      </div>
    );
  };

  render() {
    const { items, iconActive } = this.state;
    return (
      <div>
        {iconActive ? <Icon onClose={() => this.setState({ iconActive: 0 })} onSelectionDone={this.onSelection} /> : null}
        <div className="row">
          <button onClick={() => this.onAddClick()} className="btn float-right btn-sm btn-success">
            ADD
          </button>
        </div>
        {items.map(this.renderList)}
      </div>
    );
  }
}
