import React, { Component } from "react";
import Editor from "../comman/Editor";
import DropDown from "../comman/DropDown";
import UrlInput from "../comman/UrlInput";
import { Info } from "../comman/Info";
export default class CTA extends Component {
  options = [
    { label: "Redirect", value: "redirect" },
    { label: "Redirect in new tab", value: "redirectInNewTab" },
    { label: "Scroll", value: "scroll" },
  ];
  state = {
    cta: this.props.cta,
    ctaAction: this.props.ctaAction,
    redirectUrl: this.props.redirectUrl,
  };

  onChange = (value) => {
    this.setState({ cta: value }, () => {
      this.props.onChange({ cta: this.state.cta });
    });
  };

  onCtaAction = (value) => {
    this.setState({ ctaAction: value }, () => {
      this.props.onChange({ ctaAction: this.state.ctaAction });
    });
  };

  onActionChange = (e) => {
    this.setState({ redirectUrl: e.target.value }, () => {
      this.props.onChange({ redirectUrl: this.state.redirectUrl });
    });
  };

  render() {
    const { cta, ctaAction, redirectUrl } = this.state;
    return (
      <div>
        <div className="form-element">
          <div className="label-text">CTA*</div>
          <Editor value={cta} name="cta" onChange={this.onChange} />
        </div>
        <div className="form-element">
          <div className="label-text">
            CTA Action* <Info text="Action on click action button" />
          </div>
          <DropDown options={this.options} onChange={this.onCtaAction} value={ctaAction} class="dropdown-style" style={{ width: "" }} />
        </div>
        <div className="form-element">
          {ctaAction === "scroll" ? (
            <div className="label-text">
              Scroll To* <Info text="Follow Select Scroll Position by bookmark" />
            </div>
          ) : (
            <div className="label-text">
              Redirect URL* <Info text="URL to trigger on action" />
            </div>
          )}
          <input type="text" name="redirectUrl" className="form-control" value={redirectUrl} onChange={this.onActionChange} />
        </div>
      </div>
    );
  }
}
