import React, { Component } from "react";
export default class Layout extends Component {
  state = {
    layout: this.props.layout,
  };

  componentWillUnmount() {
    const cases = this.props.case || 1;
    this.props.onChange(cases, this.state.layout);
  }

  onLayoutClick = (layout) => {
    const cases = this.props.case || 1;
    this.setState({ layout });
    this.props.onChange(cases, layout);
  };

  render() {
    const { layout } = this.state;
    return (
      <div>
        <center>
          <div style={{ width: "80%", padding: "20px" }}>
            <div class="container">
              <div class="row">
                <div class="col">
                  <div className="layout-item">
                    <img src={window.rPath + "asset/Layout1.svg"} />
                    <input type="radio" checked={layout === 1 ? "checked" : ""} name="layout" onChange={() => this.onLayoutClick(1)} />
                  </div>
                </div>
                <div class="col">
                  <div className="layout-item">
                    <img src={window.rPath + "asset/Layout2.svg"} />
