import React, { Component } from "react";
import SideBar from "./SideBar";
import ToolUtil, { getTemplates, clubTemplate } from "../../utils/ToolUtil";
import Util from "../Util";
import Text from "./Text";
import CTA from "./CTA";
import { ToolLayout, ButtonsGroup } from "../layout/ToolLayout";
import { SAVE_TOOL, GET_ONE_TOOL } from "../../actions/URLs";
import { OTO2, OTO3, VIP_BONUS } from "../../utils/Routes";
import RequestHandler from "../../actions/RequestHandler";
import { Modal } from "antd";
import Style from "./Style";
import Code from "./Code";
import { OptinFormModalResponse, OptinFormModal } from "../utils/Modal";
import Time from "./Time";
import { Preview } from "./Preview";
import { ReponsiveImage } from "../comman/PreviewAble";
import { LayoutSelector } from "../layout/LayoutSelector";
import { first } from "lodash";

export default class Club extends Component {
  state = {
    toolName: "",
    toolId: "",
    preview: false,
    _id: null,
    activeTab: 1,
    toolData: {
      template: 0,
      text: "My Conversion Kit",
      cta: "Buy Now",
      layout: 0,
      timerType: "DATE_AND_TIME_BASED",
      endDateTime: "",
      timeZone: "",
      postExpiryAction: "hide",
      redirectUrl: "",
      days: 2,
      hours: 2,
      minutes: 2,
      seconds: 2,
      ctaAction: "",
      redirectUrl: "http://google.com",
      noThanks: "No thanks",
      subTitle: "Compaign To handle more",
      position: "BOTTOM",
      logo: "photo-1596907854696.png",
    },
    CODE: { include: [{ type: "contains", url: "" }], exclude: [{ type: "contains", url: null }] },
  };
  loaded = false;
  layouts = [];
  templates = null;
  componentDidMount() {
    const id = this.props.match ? (this.props.match.params ? this.props.match.params.id : null) : null;
    if (id) {
      RequestHandler.PostRequest(GET_ONE_TOOL + id, {}, (res, err) => {
        this.loaded = true;
        if (res) {
          Util.isRedirected = true;
          const data = OptinFormModalResponse(res);
          if (data) {
            this.getLayouts(data.toolData.layout);
            this.layouts = clubTemplate(data.toolData.layout, data.toolId);
            this.setState(data, () => {
              window.gs.navTitle(ToolUtil.getTool(data.toolId) + "  " + this.getLayoutName(this.state.toolData.layout) + "  " + "(" + data.name + ")");
            });
          }
        }
      });
    } else {
      const toolId = window.gs.toolId ? window.gs.toolId : 20;
      this.loaded = true;
      const defLayout = window.gs.defaultLayout || 0;
      this.onLayoutChange(defLayout);
      this.setState({ toolId: toolId, toolName: ToolUtil.getTool(toolId), name: Util.CAMPAIGNS_NAME });
      Util.RedirectWhenCampaignEmpty(this.props);
      this.getLayouts(defLayout);
      this.layouts = clubTemplate(defLayout, toolId);
      window.gs.navTitle(ToolUtil.getTool(toolId) + "  " + this.getLayoutName(defLayout) + "  " + "(" + Util.CAMPAIGNS_NAME + ")");
      delete window.gs.defaultLayout;
      delete window.gs.toolId;
    }
  }

  getLayouts = (typ) => {
    getTemplates(typ, (templates) => {
      this.templates = templates;
      this.forceUpdate();
    });
  };

  getLayoutName = (i) => {
    if (i == 0) return "Hello Bar";
    if (i == 1) return "Hello Bar + Timer";
    if (i == 2) return "Exit Intent";
  };

  templateSelect = (index) => {
    const { toolData } = this.state;
    this.setState({ toolData: { ...toolData, template: index } });
  };

  onLayoutChange = (index) => {
    const { toolData } = this.state;
    this.setState({ toolData: { ...toolData, layout: index } });
  };

  onChangeActive = (tabIndex) => {
    const { activeTab } = this.state;
    if (activeTab !== tabIndex) {
      this.setState({ activeTab: tabIndex });
    }
  };

  codeChange = (data) => {
    this.setState({ CODE: data });
  };

  onUpdate = (data) => {
    const { toolData } = this.state;
    this.setState({ toolData: { ...toolData, ...data } });
  };

  renderMain = () => {
    const { activeTab, toolData, CODE } = this.state;
    let component = null;
    if (!this.loaded) return null;
    switch (activeTab) {
      case 1:
        component = <LayoutSelector isExit={toolData.layout == 2 ? true : false} onLayoutSelect={this.templateSelect} selected={toolData.template} items={this.layouts} />;
        break;
      case 2:
        component = <Text text={toolData.text} noThanks={toolData.noThanks} subTitle={toolData.subTitle} layout={toolData.layout} onChange={this.onUpdate} />;
        break;
      case 3:
        component = <CTA cta={toolData.cta} redirectUrl={toolData.redirectUrl} ctaAction={toolData.ctaAction} position={toolData.position} layout={toolData.layout} onChange={this.onUpdate} />;
        break;
      case 4:
        component = <Style logo={toolData.logo} onChange={this.onUpdate} />;
        break;
      case 5:
        component = <Code {...CODE} onChange={this.codeChange} />;
        break;
      case 6:
        component = (
          <Time
            timerType={toolData.timerType}
            endDateTime={toolData.endDateTime}
            timeZone={toolData.timeZone}
            postExpiryAction={toolData.postExpiryAction}
            redirectUrl={toolData.redirectUrl}
            days={toolData.days}
            hours={toolData.hours}
            minutes={toolData.minutes}
            seconds={toolData.seconds}
            onChange={this.onUpdate}
          />
        );
        break;
      default:
        component = <Text onChange={this.onUpdate} />;
        break;
    }
    return component;
  };

  onClickSave = () => {
    const data = this.state;
    if (this.state === 22) data.toolData.template = this.state.toolData.template + 10;
    if (this.state.CODE.include[0].url !== "") {
      RequestHandler.PostRequest(SAVE_TOOL, { toolData: OptinFormModal(data) }, (res, err) => {
        if (res) {
          const data1 = res.data;
          if (data1.success) {
            Modal.success({
              content: data1.message,
            });
            setTimeout(() => {
              this.props.history.push("/Campaigns");
            }, 1000);
          }
        }
      });
    } else {
      Modal.warning({
        content: "Should Contain atleast one trigger point",
      });
    }
  };

  renderRight = () => {
    const { toolData, activeTab } = this.state;
    if (activeTab == 4 && toolData.logo != "") {
      return <ReponsiveImage isImg={true} url={toolData.logo} />;
    }
    if (toolData.layout === 0) {
      return <ReponsiveImage isAs={true} url={"asset/hb3.png"} />;
    }
    if (toolData.layout === 1) {
      return <ReponsiveImage isAs={true} url={"asset/hbt3.png"} />;
    }
    if (toolData.layout === 2) {
      return <ReponsiveImage isAs={true} url={"asset/exit_pop_up.png"} />;
    }
  };

  back = () => {
    if (this.state.toolId == 20) this.props.history.push(OTO3);
    else this.props.history.push(VIP_BONUS);
  };

  togglePreview = () => {
    const { preview } = this.state;
    this.setState({ preview: !preview });
  };

  render() {
    const { preview } = this.state;
    return (
      <ToolLayout>
        {preview ? <Preview {...this.state} templates={this.templates} onClose={this.togglePreview} /> : null}
        <div className="row">
          <div className="col-10 toolItem">
            <div className="row">
              <div className="col-md-6">{this.renderMain()}</div>
              <div className="col-md-6 mt-2">{this.renderRight()}</div>
              <ButtonsGroup backUrl={this.back} preview={this.togglePreview} save={this.onClickSave} />
            </div>
          </div>
          <div className="col-1 sideBarButtomPanel">
            <SideBar layout={this.state.toolData.layout} activeTab={this.state.activeTab} onChangeActive={this.onChangeActive} />
          </div>
        </div>
      </ToolLayout>
    );
  }
}
