import React, { Component } from "react";
import Editor from "../comman/Editor";
import { Switch } from "antd";
export default class Responders extends Component {
  state = {
    auto_data : [
        {
          id : 1,
          name : 'Mailchimp',
          serverName : 'MAILCHIMP',
          icon : '/asset/auto/mailchimp.png'
        },
        {
          id : 2,
          name : 'Active Campaign',
          serverName : 'ACTIVE_CAMPAIGN',
          icon : '/asset/auto/activecampaign.png'
        },
        {
          id : 3,
          name : 'Aweber',
          serverName : 'AWEBER',
          icon : '/asset/auto/aweber.png'
        },
    ]
  };

  render() {
    return (
        <div className="cz_responder_box">
            {this.state.auto_data.map((item, index) =>{
            return (
                <label key={index} onChange={(e) => this.props.onChange(item.serverName, e)}>
                    {/* <input type="checkbox" name="responder"/> */}
                    <input type="radio" name="responder"/>
                    <div className="cz_responders" key={index}>
                        <i className="fa fa-check-circle"></i>
                        <img src={item.icon} alt={item.name} /> 
                    </div>
                </label>
            )
            })}
        </div>
    );
  }
}
